mapper.select:
select
id, title, content, excerpt, cover_img, img_list, video_list, project_body, tags, classify_id,
is_home_list, status, create_time, video_url, project_url, video_img
from project


        and (title like #{keyword} or content like #{keyword})


        and tags LIKE CONCAT('%',#{searchTag},'%')


        and classify_id = #{classifyId}


        and is_home_list = #{isHomeList}


        and status = #{status}


        and status >= 0


order by create_time desc
limit #{offset}, #{count}

select count(1)
from project


        and status = #{status}

select id, title, content, excerpt, cover_img, img_list, video_list, project_body, tags, classify_id, is_home_list, status, create_time, video_url, project_url, video_img from project
YEAR(create_time) = #{year} AND MONTH(create_time) = #{month}
order by create_time desc
